import React, { useState, useEffect } from 'react';
import { X, Info, ChevronDown, Search, Filter } from 'lucide-react';

const ElectrolyteComparisonApp = () => {
  const [data, setData] = useState(null);
  const [selectedProducts, setSelectedProducts] = useState([]);
  const [detailedProduct, setDetailedProduct] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterTarget, setFilterTarget] = useState('all');
  const [sortBy, setSortBy] = useState('rating');
  const [showScoreInfo, setShowScoreInfo] = useState(false);

  // Sample data structure
  const sampleData = {
    metadata: {
      benchmark_title: "Bilan Electrolytes - Mexico Market Comparison",
      description: "A comprehensive comparison of leading electrolyte and sports drink products.",
      last_updated: "2025-10-06",
      version: "1.0"
    },
    products: [
      {
        product_id: "suerox-01",
        product_name: "Suerox",
        company: "Genomma Lab",
        price: "$35 MXN",
        overall_rating: 8.5,
        target_market: "General hydration and recovery",
        summary: {
          strengths: ["Excellent electrolyte profile", "No sugar", "Medical-grade formulation"],
          weaknesses: ["Limited flavor variety", "Higher price point"],
          best_use_cases: ["Post-illness rehydration", "Daily hydration supplement", "Athletic recovery"]
        },
        features: {
          electrolytes: [
            {
              feature_name: "Sodium (mg)",
              description: "Key electrolyte for fluid balance.",
              value: 145,
              rating: 9
            },
            {
              feature_name: "Potassium (mg)",
              description: "Essential for muscle function.",
              value: 290,
              rating: 9
            },
            {
              feature_name: "Chloride (mg)",
              description: "Helps maintain fluid balance.",
              value: 98,
              rating: 8
            }
          ],
          ingredients: [
            {
              feature_name: "Sugar-Free",
              description: "Contains no added sugars.",
              availability: "available",
              notes: "Sweetened with sucralose."
            },
            {
              feature_name: "Natural Flavors",
              description: "Uses natural flavoring agents.",
              availability: "available",
              notes: "Citrus-based flavors"
            }
          ],
          taste: [
            {
              feature_name: "Flavor Options",
              description: "Variety of available flavors.",
              value: 2,
              rating: 6,
              notes: "Original and Citrus"
            }
          ]
        }
      },
      {
        product_id: "electrolit-01",
        product_name: "Electrolit",
        company: "Pisa Farmacéutica",
        price: "$28 MXN",
        overall_rating: 8.2,
        target_market: "Sports and active lifestyle",
        summary: {
          strengths: ["Wide flavor variety", "Good taste", "Affordable"],
          weaknesses: ["Contains some sugar", "Lower electrolyte concentration"],
          best_use_cases: ["Sports hydration", "Casual exercise", "Hot weather hydration"]
        },
        features: {
          electrolytes: [
            {
              feature_name: "Sodium (mg)",
              description: "Key electrolyte for fluid balance.",
              value: 135,
              rating: 8
            },
            {
              feature_name: "Potassium (mg)",
              description: "Essential for muscle function.",
              value: 240,
              rating: 8
            },
            {
              feature_name: "Chloride (mg)",
              description: "Helps maintain fluid balance.",
              value: 85,
              rating: 7
            }
          ],
          ingredients: [
            {
              feature_name: "Sugar-Free",
              description: "Contains no added sugars.",
              availability: "partial",
              notes: "Contains 4g sugar per serving"
            },
            {
              feature_name: "Natural Flavors",
              description: "Uses natural flavoring agents.",
              availability: "available",
              notes: "Fruit-based flavors"
            }
          ],
          taste: [
            {
              feature_name: "Flavor Options",
              description: "Variety of available flavors.",
              value: 6,
              rating: 9,
              notes: "Grape, Orange, Fruit Punch, Coconut, Strawberry, Lime"
            }
          ]
        }
      },
      {
        product_id: "gatorade-01",
        product_name: "Gatorade",
        company: "PepsiCo",
        price: "$22 MXN",
        overall_rating: 7.5,
        target_market: "Sports performance",
        summary: {
          strengths: ["Widely available", "Great taste", "Trusted brand"],
          weaknesses: ["High sugar content", "Lower electrolyte levels", "Artificial ingredients"],
          best_use_cases: ["Intense workouts", "Team sports", "Endurance activities"]
        },
        features: {
          electrolytes: [
            {
              feature_name: "Sodium (mg)",
              description: "Key electrolyte for fluid balance.",
              value: 110,
              rating: 6
            },
            {
              feature_name: "Potassium (mg)",
              description: "Essential for muscle function.",
              value: 30,
              rating: 4
            },
            {
              feature_name: "Chloride (mg)",
              description: "Helps maintain fluid balance.",
              value: 0,
              rating: 0
            }
          ],
          ingredients: [
            {
              feature_name: "Sugar-Free",
              description: "Contains no added sugars.",
              availability: "not available",
              notes: "Contains 14g sugar per serving"
            },
            {
              feature_name: "Natural Flavors",
              description: "Uses natural flavoring agents.",
              availability: "partial",
              notes: "Mix of natural and artificial flavors"
            }
          ],
          taste: [
            {
              feature_name: "Flavor Options",
              description: "Variety of available flavors.",
              value: 8,
              rating: 10,
              notes: "Multiple classic and limited edition flavors"
            }
          ]
        }
      },
      {
        product_id: "powerade-01",
        product_name: "Powerade",
        company: "Coca-Cola",
        price: "$20 MXN",
        overall_rating: 7.2,
        target_market: "Sports and fitness",
        summary: {
          strengths: ["Affordable", "Good availability", "B-vitamins added"],
          weaknesses: ["High sugar content", "Moderate electrolyte levels", "Artificial colors"],
          best_use_cases: ["Moderate exercise", "Budget-conscious athletes", "General hydration"]
        },
        features: {
          electrolytes: [
            {
              feature_name: "Sodium (mg)",
              description: "Key electrolyte for fluid balance.",
              value: 100,
              rating: 6
            },
            {
              feature_name: "Potassium (mg)",
              description: "Essential for muscle function.",
              value: 25,
              rating: 3
            },
            {
              feature_name: "Chloride (mg)",
              description: "Helps maintain fluid balance.",
              value: 0,
              rating: 0
            }
          ],
          ingredients: [
            {
              feature_name: "Sugar-Free",
              description: "Contains no added sugars.",
              availability: "not available",
              notes: "Contains 15g sugar per serving"
            },
            {
              feature_name: "Natural Flavors",
              description: "Uses natural flavoring agents.",
              availability: "not available",
              notes: "Primarily artificial flavors and colors"
            }
          ],
          taste: [
            {
              feature_name: "Flavor Options",
              description: "Variety of available flavors.",
              value: 5,
              rating: 8,
              notes: "Mountain Berry Blast, Fruit Punch, Orange, Grape, Lemon Lime"
            }
          ]
        }
      }
    ],
    scoring_system: {
      weighting_factors: {
        electrolytes: 0.5,
        ingredients: 0.3,
        taste: 0.2
      },
      calculation_methodology: "Overall score is a weighted average of category ratings, with electrolyte content given highest priority."
    }
  };

  useEffect(() => {
    // Simulate loading data
    setData(sampleData);
  }, []);

  const renderStars = (rating) => {
    const stars = [];
    const fullStars = Math.floor(rating / 2);
    const hasHalfStar = rating % 2 >= 1;

    for (let i = 0; i < fullStars; i++) {
      stars.push(<span key={i} className="text-yellow-400">★</span>);
    }
    if (hasHalfStar) {
      stars.push(<span key="half" className="text-yellow-400">⯨</span>);
    }
    for (let i = stars.length; i < 5; i++) {
      stars.push(<span key={`empty-${i}`} className="text-gray-300">★</span>);
    }
    return stars;
  };

  const toggleProductSelection = (productId) => {
    setSelectedProducts(prev => {
      if (prev.includes(productId)) {
        return prev.filter(id => id !== productId);
      } else if (prev.length < 3) {
        return [...prev, productId];
      }
      return prev;
    });
  };

  const getAvailabilityBadge = (availability) => {
    const colors = {
      available: 'bg-green-100 text-green-800',
      'not available': 'bg-red-100 text-red-800',
      partial: 'bg-yellow-100 text-yellow-800'
    };
    return colors[availability] || 'bg-gray-100 text-gray-800';
  };

  const getFilteredAndSortedProducts = () => {
    if (!data) return [];

    let filtered = data.products.filter(product => {
      const matchesSearch = product.product_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          product.company.toLowerCase().includes(searchTerm.toLowerCase());
      const matchesFilter = filterTarget === 'all' || product.target_market === filterTarget;
      return matchesSearch && matchesFilter;
    });

    return filtered.sort((a, b) => {
      if (sortBy === 'rating') return b.overall_rating - a.overall_rating;
      if (sortBy === 'price') {
        const priceA = parseInt(a.price.replace(/[^0-9]/g, ''));
        const priceB = parseInt(b.price.replace(/[^0-9]/g, ''));
        return priceA - priceB;
      }
      return 0;
    });
  };

  if (!data) {
    return <div className="flex items-center justify-center h-screen">Loading...</div>;
  }

  const filteredProducts = getFilteredAndSortedProducts();
  const targetMarkets = ['all', ...new Set(data.products.map(p => p.target_market))];

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-blue-600 text-white shadow-lg">
        <div className="container mx-auto px-4 py-6">
          <h1 className="text-3xl font-bold">{data.metadata.benchmark_title}</h1>
          <p className="text-blue-100 mt-2">{data.metadata.description}</p>
          <div className="flex items-center gap-4 mt-3 text-sm">
            <span>Version {data.metadata.version}</span>
            <span>•</span>
            <span>Last Updated: {data.metadata.last_updated}</span>
          </div>
        </div>
      </header>

      {/* Controls */}
      <div className="bg-white shadow-sm border-b">
        <div className="container mx-auto px-4 py-4">
          <div className="flex flex-wrap gap-4">
            {/* Search */}
            <div className="flex-1 min-w-64">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
                <input
                  type="text"
                  placeholder="Search products or companies..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>
            </div>

            {/* Filter */}
            <div className="relative">
              <Filter className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-gray-400" />
              <select
                value={filterTarget}
                onChange={(e) => setFilterTarget(e.target.value)}
                className="pl-10 pr-8 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 appearance-none bg-white"
              >
                {targetMarkets.map(market => (
                  <option key={market} value={market}>
                    {market === 'all' ? 'All Markets' : market}
                  </option>
                ))}
              </select>
              <ChevronDown className="absolute right-3 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-400 pointer-events-none" />
            </div>

            {/* Sort */}
            <div className="relative">
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="pl-4 pr-8 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 appearance-none bg-white"
              >
                <option value="rating">Sort by Rating</option>
                <option value="price">Sort by Price</option>
              </select>
              <ChevronDown className="absolute right-3 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-400 pointer-events-none" />
            </div>
          </div>

          {selectedProducts.length > 0 && (
            <div className="mt-4">
              <button
                onClick={() => setSelectedProducts([])}
                className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition"
              >
                Compare Selected ({selectedProducts.length})
              </button>
              <span className="ml-3 text-sm text-gray-600">Select up to 3 products</span>
            </div>
          )}
        </div>
      </div>

      <div className="container mx-auto px-4 py-8">
        {/* Comparison View */}
        {selectedProducts.length >= 2 && (
          <div className="mb-8 bg-white rounded-lg shadow-lg p-6">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-2xl font-bold">Product Comparison</h2>
              <button
                onClick={() => setSelectedProducts([])}
                className="text-gray-500 hover:text-gray-700"
              >
                <X className="w-6 h-6" />
              </button>
            </div>

            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="border-b-2">
                    <th className="text-left py-3 px-2 font-semibold">Feature</th>
                    {selectedProducts.map(id => {
                      const product = data.products.find(p => p.product_id === id);
                      return (
                        <th key={id} className="text-left py-3 px-4">
                          <div className="font-bold">{product.product_name}</div>
                          <div className="text-sm text-gray-600 font-normal">{product.company}</div>
                        </th>
                      );
                    })}
                  </tr>
                </thead>
                <tbody>
                  <tr className="border-b bg-gray-50">
                    <td className="py-3 px-2 font-semibold">Price</td>
                    {selectedProducts.map(id => {
                      const product = data.products.find(p => p.product_id === id);
                      return <td key={id} className="py-3 px-4">{product.price}</td>;
                    })}
                  </tr>
                  <tr className="border-b bg-gray-50">
                    <td className="py-3 px-2 font-semibold">Overall Rating</td>
                    {selectedProducts.map(id => {
                      const product = data.products.find(p => p.product_id === id);
                      return (
                        <td key={id} className="py-3 px-4">
                          <div className="flex items-center gap-2">
                            {renderStars(product.overall_rating)}
                            <span className="text-sm">({product.overall_rating})</span>
                          </div>
                        </td>
                      );
                    })}
                  </tr>

                  {/* Electrolytes Section */}
                  <tr className="border-b bg-blue-50">
                    <td colSpan={selectedProducts.length + 1} className="py-2 px-2 font-bold text-blue-900">
                      Electrolytes
                    </td>
                  </tr>
                  {selectedProducts.length > 0 && 
                    data.products.find(p => p.product_id === selectedProducts[0]).features.electrolytes.map((feature, idx) => (
                      <tr key={`electrolyte-${idx}`} className="border-b">
                        <td className="py-3 px-2">
                          <div className="font-medium">{feature.feature_name}</div>
                          <div className="text-xs text-gray-600">{feature.description}</div>
                        </td>
                        {selectedProducts.map(id => {
                          const product = data.products.find(p => p.product_id === id);
                          const feat = product.features.electrolytes[idx];
                          return (
                            <td key={id} className="py-3 px-4">
                              <div className="font-semibold">{feat.value}</div>
                              <div className="flex items-center gap-1 text-sm">
                                {renderStars(feat.rating)}
                              </div>
                            </td>
                          );
                        })}
                      </tr>
                    ))
                  }

                  {/* Ingredients Section */}
                  <tr className="border-b bg-green-50">
                    <td colSpan={selectedProducts.length + 1} className="py-2 px-2 font-bold text-green-900">
                      Ingredients
                    </td>
                  </tr>
                  {selectedProducts.length > 0 && 
                    data.products.find(p => p.product_id === selectedProducts[0]).features.ingredients.map((feature, idx) => (
                      <tr key={`ingredient-${idx}`} className="border-b">
                        <td className="py-3 px-2">
                          <div className="font-medium">{feature.feature_name}</div>
                          <div className="text-xs text-gray-600">{feature.description}</div>
                        </td>
                        {selectedProducts.map(id => {
                          const product = data.products.find(p => p.product_id === id);
                          const feat = product.features.ingredients[idx];
                          return (
                            <td key={id} className="py-3 px-4">
                              <span className={`inline-block px-2 py-1 rounded text-xs font-semibold ${getAvailabilityBadge(feat.availability)}`}>
                                {feat.availability}
                              </span>
                              {feat.notes && <div className="text-xs text-gray-600 mt-1">{feat.notes}</div>}
                            </td>
                          );
                        })}
                      </tr>
                    ))
                  }

                  {/* Taste Section */}
                  <tr className="border-b bg-purple-50">
                    <td colSpan={selectedProducts.length + 1} className="py-2 px-2 font-bold text-purple-900">
                      Taste
                    </td>
                  </tr>
                  {selectedProducts.length > 0 && 
                    data.products.find(p => p.product_id === selectedProducts[0]).features.taste.map((feature, idx) => (
                      <tr key={`taste-${idx}`} className="border-b">
                        <td className="py-3 px-2">
                          <div className="font-medium">{feature.feature_name}</div>
                          <div className="text-xs text-gray-600">{feature.description}</div>
                        </td>
                        {selectedProducts.map(id => {
                          const product = data.products.find(p => p.product_id === id);
                          const feat = product.features.taste[idx];
                          return (
                            <td key={id} className="py-3 px-4">
                              <div className="font-semibold">{feat.value} flavors</div>
                              <div className="flex items-center gap-1 text-sm">
                                {renderStars(feat.rating)}
                              </div>
                              {feat.notes && <div className="text-xs text-gray-600 mt-1">{feat.notes}</div>}
                            </td>
                          );
                        })}
                      </tr>
                    ))
                  }
                </tbody>
              </table>
            </div>
          </div>
        )}

        {/* Product Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
          {filteredProducts.map(product => (
            <div
              key={product.product_id}
              className={`bg-white rounded-lg shadow-md hover:shadow-xl transition cursor-pointer border-2 ${
                selectedProducts.includes(product.product_id) ? 'border-blue-500' : 'border-transparent'
              }`}
            >
              <div className="p-6">
                <div className="flex justify-between items-start mb-4">
                  <div className="flex-1">
                    <h3 
                      className="text-xl font-bold text-gray-900 hover:text-blue-600"
                      onClick={() => setDetailedProduct(product)}
                    >
                      {product.product_name}
                    </h3>
                    <p className="text-sm text-gray-600">{product.company}</p>
                  </div>
                  <input
                    type="checkbox"
                    checked={selectedProducts.includes(product.product_id)}
                    onChange={() => toggleProductSelection(product.product_id)}
                    className="w-5 h-5 text-blue-600 cursor-pointer"
                    disabled={!selectedProducts.includes(product.product_id) && selectedProducts.length >= 3}
                  />
                </div>

                <div className="flex items-center gap-2 mb-3">
                  {renderStars(product.overall_rating)}
                  <span className="text-sm font-semibold">({product.overall_rating})</span>
                  <button
                    onMouseEnter={() => setShowScoreInfo(product.product_id)}
                    onMouseLeave={() => setShowScoreInfo(false)}
                    className="relative ml-1"
                  >
                    <Info className="w-4 h-4 text-gray-400 hover:text-gray-600" />
                    {showScoreInfo === product.product_id && (
                      <div className="absolute z-10 bottom-full left-0 mb-2 w-64 p-3 bg-gray-900 text-white text-xs rounded-lg shadow-lg">
                        {data.scoring_system.calculation_methodology}
                        <div className="mt-2">
                          Weights: Electrolytes {data.scoring_system.weighting_factors.electrolytes * 100}%, 
                          Ingredients {data.scoring_system.weighting_factors.ingredients * 100}%, 
                          Taste {data.scoring_system.weighting_factors.taste * 100}%
                        </div>
                      </div>
                    )}
                  </button>
                </div>

                <div className="mb-4">
                  <span className="text-2xl font-bold text-blue-600">{product.price}</span>
                </div>

                <div className="mb-4">
                  <span className="text-xs bg-gray-100 px-2 py-1 rounded text-gray-700">
                    {product.target_market}
                  </span>
                </div>

                <button
                  onClick={() => setDetailedProduct(product)}
                  className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition"
                >
                  View Details
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Detailed Product Modal */}
      {detailedProduct && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
            <div className="sticky top-0 bg-white border-b p-6 flex justify-between items-start">
              <div>
                <h2 className="text-3xl font-bold">{detailedProduct.product_name}</h2>
                <p className="text-gray-600">{detailedProduct.company}</p>
              </div>
              <button
                onClick={() => setDetailedProduct(null)}
                className="text-gray-500 hover:text-gray-700"
              >
                <X className="w-6 h-6" />
              </button>
            </div>

            <div className="p-6">
              <div className="grid grid-cols-2 gap-4 mb-6">
                <div>
                  <div className="text-sm text-gray-600">Price</div>
                  <div className="text-2xl font-bold text-blue-600">{detailedProduct.price}</div>
                </div>
                <div>
                  <div className="text-sm text-gray-600">Overall Rating</div>
                  <div className="flex items-center gap-2">
                    {renderStars(detailedProduct.overall_rating)}
                    <span className="font-semibold">({detailedProduct.overall_rating})</span>
                  </div>
                </div>
              </div>

              <div className="mb-6">
                <div className="text-sm text-gray-600 mb-1">Target Market</div>
                <span className="bg-gray-100 px-3 py-1 rounded text-sm">{detailedProduct.target_market}</span>
              </div>

              {/* Summary */}
              <div className="mb-6">
                <h3 className="text-xl font-bold mb-3">Summary</h3>

                <div className="mb-4">
                  <h4 className="font-semibold text-green-700 mb-2">Strengths</h4>
                  <ul className="list-disc list-inside space-y-1">
                    {detailedProduct.summary.strengths.map((strength, idx) => (
                      <li key={idx} className="text-gray-700">{strength}</li>
                    ))}
                  </ul>
                </div>

                <div className="mb-4">
                  <h4 className="font-semibold text-red-700 mb-2">Weaknesses</h4>
                  <ul className="list-disc list-inside space-y-1">
                    {detailedProduct.summary.weaknesses.map((weakness, idx) => (
                      <li key={idx} className="text-gray-700">{weakness}</li>
                    ))}
                  </ul>
                </div>

                <div>
                  <h4 className="font-semibold text-blue-700 mb-2">Best Use Cases</h4>
                  <ul className="list-disc list-inside space-y-1">
                    {detailedProduct.summary.best_use_cases.map((useCase, idx) => (
                      <li key={idx} className="text-gray-700">{useCase}</li>
                    ))}
                  </ul>
                </div>
              </div>

              {/* Features */}
              <div>
                <h3 className="text-xl font-bold mb-3">Detailed Features</h3>

                {/* Electrolytes */}
                <div className="mb-6">
                  <h4 className="font-semibold text-lg text-blue-900 mb-3 bg-blue-50 px-3 py-2 rounded">
                    Electrolytes
                  </h4>
                  <div className="space-y-3">
                    {detailedProduct.features.electrolytes.map((feature, idx) => (
                      <div key={idx} className="border-l-4 border-blue-500 pl-4">
                        <div className="font-semibold">{feature.feature_name}: {feature.value}</div>
                        <div className="text-sm text-gray-600">{feature.description}</div>
